  width: 260px;
  box-shadow: 0 0 10px 2px rgba(0, 0, 0, 0.6);
  padding: 20px;
}

nav h1 {
  font-weight: 300;
  font-size: 40px;
}

nav p {
  font-weight: 300;
  color: $secondary-color;
  font-size: 18px;
  margin-top: 5px;
  padding-top: 5px;
  border-top: 1px dashed $secondary-color;
  text-transform: uppercase;
}

.flex-center {
  display: flex;
  justify-content: center;
  align-items: center;
}

.content-topic {
  flex-grow: 1;
  margin: 30px 50px;
}

.container {
  display: grid;
  grid-template-columns: 1fr 4fr;
  grid-gap: 20px;
}

.image-box {
